Condition.Update:
(A:SIM ON GROUND, bool) ! if{
	(A:ELEVATOR TRIM DISABLED, bool) if{ (>K:ELEVATOR_TRIM_DISABLED_TOGGLE) }
	(A:AILERON TRIM DISABLED, bool) if{ (>K:AILERON_TRIM_DISABLED_TOGGLE) }
}

False.DRAG_CODE:
(O:_LastPos) (O:StartPos) - abs 0.001 > if{
	(A:RUDDER TRIM PCT, Percent) #DRAG_INCREMENT# (O:DragScale) * + #ANIM_SIMVAR_MAX# min #ANIM_SIMVAR_MIN# max (>K:RUDDER_TRIM_SET)
}

UseTemplate.RELEASE_CODE:
(O:_LastPos) (O:StartPos) - abs 0.001 < if{
	(O:StartPos) 0 > if{
		g1
	} els{
		g2
	}
}

UseTemplate.LEFT_SINGLE_CODE:
(A:ANTISKID BRAKES ACTIVE, Bool) ! (L:XMLVAR_Autobrakes_Level) #AUTOBRAKE_LEVEL# == or if{
	0 (>L:XMLVAR_Autobrakes_Level)
} els{
	#AUTOBRAKE_LEVEL# (>L:XMLVAR_Autobrakes_Level)
}

(L:XMLVAR_Autobrakes_Level) 0 == if{
	1 (>K:SET_AUTOBRAKE_CONTROL)
} els{
	(A:SIM ON GROUND, Bool) if{
		0 (>K:SET_AUTOBRAKE_CONTROL)
	} els{
		(L:XMLVAR_Autobrakes_Level) 1 + (>K:SET_AUTOBRAKE_CONTROL)
	}
}

UseTemplate.UPDATE_CODE:
(A:AUTO BRAKE SWITCH CB, Enum) s1 0 == if{
	(L:XMLVAR_Autobrakes_Level) 0 == if{
		#AUTOBRAKE_LEVEL# (>L:XMLVAR_Autobrakes_Level)
	}
} els{
	l1 1 == if{
		0 (>L:XMLVAR_Autobrakes_Level)
	} els{
		l1 1 - (>L:XMLVAR_Autobrakes_Level)
		(A:SIM ON GROUND, Bool) (A:GROUND VELOCITY, Knots) 10 < and if{
			0 (>K:SET_AUTOBRAKE_CONTROL)
		}
	}
}

True.CLOCKWISE_CODE:
(A:AUTO BRAKE SWITCH CB, Enum) 1 ==
(L:XMLVar_Autobrake_Disarm) ! and if{
	1 (>L:XMLVar_Autobrake_Disarm)
} els{
	(>K:INCREASE_AUTOBRAKE_CONTROL)
}

True.ANTICLOCKWISE_CODE:
(A:AUTO BRAKE SWITCH CB, Enum) 1 ==
(L:XMLVar_Autobrake_Disarm) and if{
	0 (>L:XMLVar_Autobrake_Disarm)
} els{
	(>K:DECREASE_AUTOBRAKE_CONTROL)
}

True.UPDATE_CODE:
#ARMED_STATE_TEST# if{
	1 (>#POSITION_TYPE#:#POSITION_VAR#)
} els{
	(A:SPOILERS HANDLE POSITION, Percent over 100) 0 == if{
		0 (>#POSITION_TYPE#:#POSITION_VAR#)
	} els{
		1 (A:SPOILERS HANDLE POSITION, Percent over 100) #STEPS_NUMBER# * + (>#POSITION_TYPE#:#POSITION_VAR#)
	}
}

False.UPDATE_CODE:
#ARMED_STATE_TEST# if{
	0 (>#POSITION_TYPE#:#POSITION_VAR#)
} els{
	1 (A:SPOILERS HANDLE POSITION, Percent over 100) #STEPS_NUMBER# * + (>#POSITION_TYPE#:#POSITION_VAR#)
}

UseTemplate.DRAG_CODE:
(#POSITION_TYPE#:#POSITION_VAR#)  1 <= if{
	(M:DragPercent) 0 != #ARMED_POSITION_INVERSION# if{
		#RETRACT_POSITION# (>#POSITION_TYPE#:#POSITION_VAR#)
		(>K:SPOILERS_ARM_OFF)
		0 (>K:SPOILERS_SET)
	} els{
		(M:DragPercent) 0 == #ARMED_POSITION_INVERSION# if{
			#ARMED_POSITION# (>#POSITION_TYPE#:#POSITION_VAR#)
			(>K:SPOILERS_ARM_ON)
			0 (>K:SPOILERS_SET)
		}
	}
} els{
	(#POSITION_TYPE#:#POSITION_VAR#) 1 - #STEPS_NUMBER# / 16384 * (>K:SPOILERS_SET)
	(A:SPOILERS ARMED, Bool) if{ (>K:SPOILERS_ARM_OFF) }
}

(M:DragPercent) sp0
(A:FLAPS HANDLE INDEX, number) #MAX_HANDLE_INDEX# / sp1
l0 l1 - sp2
l2 abs #MIN_DELTA# > if{
	l2 0 > if{
		(>K:FLAPS_INCR)
	} els{
		(>K:FLAPS_DECR)
	}
}

UseTemplate.LEFT_SINGLE_CODE:
(O:XMLVAR_TrimFlaps_Disabled) ! (>O:XMLVAR_TrimFlaps_Disabled)
(O:XMLVAR_TrimFlaps_Disabled) if{
	#DISABLE_TRIM#
	#BUS_ID# (>A:BUS LOOKUP INDEX, Number) (A:CIRCUIT CONNECTION ON:#CIRCUIT_ID#, Bool) 0 != if{
		#CIRCUIT_ID# #BUS_ID# (>K:2:ELECTRICAL_BUS_TO_CIRCUIT_CONNECTION_TOGGLE)
	}
} els{
	#ENABLE_TRIM#
	#BUS_ID# (>A:BUS LOOKUP INDEX, Number) (A:CIRCUIT CONNECTION ON:#CIRCUIT_ID#, Bool) 1 != if{
		#CIRCUIT_ID# #BUS_ID# (>K:2:ELECTRICAL_BUS_TO_CIRCUIT_CONNECTION_TOGGLE)
	}
}

UseTemplate.DRAG_CODE:
(M:DragPercent) sp0
(A:FLAPS HANDLE INDEX, number) #MAX_HANDLE_INDEX# / sp1
l0 l1 - sp2
l2 abs #MIN_DELTA# > if{
	l2 0 > if{
		(>K:FLAPS_INCR)
	} els{
		(>K:FLAPS_DECR)
	}
}

