	#ESSENTIAL_BUS_ID# #MAIN_BUS_ID# (>K:2:ELECTRICAL_BUS_TO_BUS_CONNECTION_TOGGLE)
}
(* Redirect power relay coil *)
(L:XMLVAR_Essential_Bus_ON) if{
	#MAIN_BUS_ID# (>A:BUS LOOKUP INDEX, Number) (A:BUS CONNECTION ON:#BATTERY_BUS_ID#, Bool) if{
		#MAIN_BUS_ID# #BATTERY_BUS_ID# (>K:2:ELECTRICAL_BUS_TO_BUS_CONNECTION_TOGGLE)
	}
	#ESSENTIAL_BUS_ID# (>A:BUS LOOKUP INDEX, Number) (A:BUS CONNECTION ON:#BATTERY_BUS_ID#, Bool) ! if{
		#ESSENTIAL_BUS_ID# #BATTERY_BUS_ID# (>K:2:ELECTRICAL_BUS_TO_BUS_CONNECTION_TOGGLE)
	}
} els{
	#MAIN_BUS_ID# (>A:BUS LOOKUP INDEX, Number) (A:BUS CONNECTION ON:#BATTERY_BUS_ID#, Bool) ! if{
		#MAIN_BUS_ID# #BATTERY_BUS_ID# (>K:2:ELECTRICAL_BUS_TO_BUS_CONNECTION_TOGGLE)
	}
	#ESSENTIAL_BUS_ID# (>A:BUS LOOKUP INDEX, Number) (A:BUS CONNECTION ON:#BATTERY_BUS_ID#, Bool) if{
		#ESSENTIAL_BUS_ID# #BATTERY_BUS_ID# (>K:2:ELECTRICAL_BUS_TO_BUS_CONNECTION_TOGGLE)
	}
}
(L:XMLVAR_Essential_Bus_ON) (>O:_Last_XMLVAR_Essential_Bus_ON)

