UseTemplate.UPDATE_CODE:
#XMLVAR_VALUE# (A:AUTOPILOT MASTER, Bool) s0 != if{
	l0 if{
		0 #XMLVAR_SETTER#
		1 (>L:XMLVAR_Autopilot_1_Status) 
	} els{
		l0 #XMLVAR_SETTER#
	}
}

Case.UPDATE_CODE:
(A:AUTOPILOT MASTER, Bool) (L:XMLVAR_Autopilot_1_Status) (L:XMLVAR_Autopilot_2_Status) or (L:XMLVAR_Autopilot_3_Status) or != if{
	#RESET_CODE#
	(A:AUTOPILOT MASTER, Bool) (>L:XMLVAR_Autopilot_#ID#_Status) 
}

(A:AUTOPILOT MASTER, Bool) (L:XMLVAR_Autopilot_1_Status) (L:XMLVAR_Autopilot_2_Status) or != if{
	#RESET_CODE#
	(A:AUTOPILOT MASTER, Bool) (>L:XMLVAR_Autopilot_#ID#_Status) 
}

Case.LEFT_SINGLE_CODE:
(A:AUTOPILOT DISENGAGED, Bool) ! #TEST_CODE# and if{
	#RESET_CODE#
	(L:XMLVAR_Autopilot_#ID#_Status) ! (>L:XMLVAR_Autopilot_#ID#_Status)
	(L:XMLVAR_Autopilot_1_Status) (L:XMLVAR_Autopilot_2_Status) or (L:XMLVAR_Autopilot_3_Status) or
	(A:AUTOPILOT MASTER, Bool) != if{
		(>K:AP_MASTER)
		(A:AUTOPILOT MASTER, Bool) ! if{
			0 (>L:XMLVAR_Autopilot_#ID#_Status) 
			(>H:Generic_Autopilot_#ID#_Manual_Off)
		}
	}
}

(A:AUTOPILOT DISENGAGED, Bool) ! #TEST_CODE# and  if{
	#RESET_CODE#
	(L:XMLVAR_Autopilot_#ID#_Status) ! (>L:XMLVAR_Autopilot_#ID#_Status)
	(L:XMLVAR_Autopilot_1_Status) (L:XMLVAR_Autopilot_2_Status) or
	(A:AUTOPILOT MASTER, Bool) != if{
		(>K:AP_MASTER)
		(A:AUTOPILOT MASTER, Bool) ! if{
			0 (>L:XMLVAR_Autopilot_#ID#_Status) 
			(>H:Generic_Autopilot_#ID#_Manual_Off)
		}
	}
}

Default.LEFT_SINGLE_CODE:
(A:AUTOPILOT DISENGAGED, Bool) ! if{
	(>K:AP_MASTER) #ON_AP_MASTER_SWITCH#
	(A:AUTOPILOT MASTER, Bool) ! if{
		(>H:Generic_Autopilot_Manual_Off)
	}
}

UseTemplate.LEFT_SINGLE_CODE:
#GPS_DISABLE_CODE# 
(A:AUTOPILOT APPROACH HOLD, Bool) (A:AUTOPILOT GLIDESLOPE HOLD, Bool) ! and if{ (>K:AP_APR_HOLD) }
(>K:AP_APR_HOLD)

UseTemplate.UPDATE_CODE:
(A:AUTOPILOT MASTER, Bool) ! if{
	(A:AUTOPILOT WING LEVELER, Bool) if{
		(>K:AP_WING_LEVELER_OFF)
		(>K:AP_PITCH_LEVELER_OFF)
	}
}

UseTemplate.LEFT_SINGLE_CODE:
(>K:AP_WING_LEVELER) 
(A:AUTOPILOT WING LEVELER, Bool) if{
	(A:AUTOPILOT MASTER, Bool) (>O:APStateWhenLevelerEnabled)
	(A:AUTOPILOT MASTER, Bool) ! if{
		(>K:AUTOPILOT_ON)
	}
	(>K:AP_PITCH_LEVELER_ON)
} els{
	(>K:AP_PITCH_LEVELER_OFF)
	(O:APStateWhenLevelerEnabled) if{
		(>K:AUTOPILOT_ON)
	} els{
		(>K:AUTOPILOT_OFF)
	}
}

Default.LEFT_SINGLE_CODE:
#ID# (>K:AUTO_THROTTLE_ARM)
(A:FLAPS HANDLE INDEX, Number) 0 > if{ 
	(A:DESIGN SPEED VS0, knots) sp1 
} els{
	(A:DESIGN SPEED VS1, knots) sp1 
}
l1 1.3 * (A:AUTOPILOT AIRSPEED HOLD VAR:0, knots) max (>K:AP_SPD_VAR_SET)

UseTemplate.LEFT_SINGLE_CODE:
(A:AUTOPILOT MASTER, Bool) if{
	(* Turns off AP then turns it back on to go back to default modes. *)
	(>K:AUTOPILOT_OFF)
	(>K:AUTOPILOT_ON)
}

UseTemplate.UPDATE_CODE:
(A:AUTOPILOT FLIGHT LEVEL CHANGE, Bool) ! if{
	(L:XMLVAR_AirSpeedIsInMach) if{
		(>K:AP_MACH_ON)
	} els{
		(>K:AP_AIRSPEED_ON)
	}
}

UseTemplate.CLOCKWISE_CODE:
1 (>L:XMLVAR_SpeedIsManuallySet)
(A:FLAPS HANDLE INDEX, Number) 0 > if{ 
	(A:DESIGN SPEED VS0, knots) sp1 
} els{
	(A:DESIGN SPEED VS1, knots) sp1 
}
l1 1.3 * (A:AIRSPEED INDICATED, knots) max (>K:AP_SPD_VAR_SET)
