Component.Update:
(* This update forces the PROP & MIXTURE Levers to specific values, which may conflict with the AutoStart, so only run it while the autostart is disabled, using the fuel trigger which is linked to the autostart *)
(A:FUELSYSTEM TRIGGER STATUS:1, Bool) ! if{
	(A:GENERAL ENG MIXTURE LEVER POSITION:#ID#, percent over 100) 0.01 < if{
		(#POSITION_TYPE#:#POSITION_VAR#) 1 >= if{
			0 (>#POSITION_TYPE#:#POSITION_VAR#)
			-4096 (>K:PROP_PITCH#ID#_SET) (* Fuel Cutoff *)
		}
	} els{
		(A:GENERAL ENG MIXTURE LEVER POSITION:#ID#, percent over 100) 0.99 < if{
			1 (A:GENERAL ENG MIXTURE LEVER POSITION:#ID#, percent over 100) 0.1 - 1.111 * + (>#POSITION_TYPE#:#POSITION_VAR#)
			-4096 (>K:PROP_PITCH#ID#_SET) (* Feathering *)
		} els{
			(A:GENERAL ENG MIXTURE LEVER POSITION:#ID#, percent over 100) 0.99 >= if{
				2 (A:GENERAL ENG PROPELLER LEVER POSITION:#ID#, percent over 100) + (>#POSITION_TYPE#:#POSITION_VAR#) (* Auto *)
			}
		}
	}
}


	0 (>L:XML_ConditionLever#ID#_Cutoff)
	0 (>L:XML_ConditionLever#ID#_Feathering)
	0 (>L:XML_ConditionLever#ID#_Auto)
	0 (>L:XML_ConditionLever#ID#_Override)

	(#POSITION_TYPE#:#POSITION_VAR#) 1 < if{
		1 (>L:XML_ConditionLever#ID#_Cutoff)
	} els{
		(#POSITION_TYPE#:#POSITION_VAR#) 2 < if{
		1 (>L:XML_ConditionLever#ID#_Feathering)
		} els{
			(#POSITION_TYPE#:#POSITION_VAR#) 3 < if{
				1 (>L:XML_ConditionLever#ID#_Auto)
			} els{
				(#POSITION_TYPE#:#POSITION_VAR#) 4 < if{
					1 (>L:XML_ConditionLever#ID#_Override)
				}
			}
		}
	}
(#POSITION_TYPE#:#POSITION_VAR#) int (#POSITION_TYPE#:#POSITION_VAR#) - abs (>L:XML_ConditionLever#ID#_Value)

UseTemplate.DRAG_CODE:
(#POSITION_TYPE#:#POSITION_VAR#) 1 < if{
	0 (>K:MIXTURE#ID#_SET) (* Fuel Cutoff *)
	-4096 (>K:PROP_PITCH#ID#_SET)
} els{
	(#POSITION_TYPE#:#POSITION_VAR#) 2 < if{
		(#POSITION_TYPE#:#POSITION_VAR#) 1 - 14745.6 * 1638.4 + (>K:MIXTURE#ID#_SET) (* Sets mixture from 0.1 to 1 *)
		-4096 (>K:PROP_PITCH#ID#_SET)(* Feathering *)
	} els{
		(#POSITION_TYPE#:#POSITION_VAR#) 3 < if{
			16384 (>K:MIXTURE#ID#_SET)
			(#POSITION_TYPE#:#POSITION_VAR#) 2 - 16384 * (>K:PROP_PITCH#ID#_SET)(* Auto *)
		} els{
			(#POSITION_TYPE#:#POSITION_VAR#) 4 <= if{
				16384 (>K:MIXTURE#ID#_SET)
				16384 (>K:PROP_PITCH#ID#_SET)(* Override *)
			}
		}
	}
}

