UseTemplate.ANIM_CODE:
(A:INDICATED ALTITUDE, feet) 0 >=
 if{ (A:INDICATED ALTITUDE, feet) #FEET# 10 / / 100 % }
els{ (A:INDICATED ALTITUDE, feet) #FEET# 10 / / 100 % 100 + }

False.LOCALIZER_ANIM_CODE:
(A:GPS DRIVES NAV1, Bool) if{
	(A:GPS WP CROSS TRK, nautical mile) 127 * 127 +
} els{
	(A:NAV CDI:#ID#, Number) 127 +
}

False.Update:
(* Avionics switch watcher *)
#IS_AVIONICS_ON# sp0
l0 (O:XMLVAR_Avionics_ON) != if{
	l0 if{
		1 (>O:XMLVAR_Needles_Init_Animation_Playing)
		0 (>O:XMLVAR_AnimationStep)
	} els{
		#FINAL_STEP#
	}
}
l0 (>O:XMLVAR_Avionics_ON)
(* Animation steps handling *)
(O:XMLVAR_Needles_Init_Animation_Playing) if{
	(O:XMLVAR_UpdateCount) #UPDATE_SKIPPED_COUNT# % 0 == if{
		(O:XMLVAR_AnimationStep) 0 == if{
			#FIRST_STEP#
		} els{
			(O:XMLVAR_AnimationStep) 1 == if{
				#ANIM_STEP_1#
			} els{
				(O:XMLVAR_AnimationStep) 2 == if{
					#ANIM_STEP_2#
				} els{
					(O:XMLVAR_AnimationStep) 3 == if{
						#ANIM_STEP_3#
					} els{
						(O:XMLVAR_AnimationStep) 4 == if{
							#FINAL_STEP#
						} 
					}
				}
			}
		}
		(O:XMLVAR_AnimationStep) 1 + (>O:XMLVAR_AnimationStep)
	}
	(O:XMLVAR_UpdateCount) 1 + (>O:XMLVAR_UpdateCount)
}

Case.ANIM_CODE:
#SELECTION# #MAX# min #MIN# max sp0
l0 #REF_LITERS_LOW# > if{
	l0 #REF_LITERS_HIGH# > if{
		(* Over High *)
		l0 #REF_LITERS_HIGH# - #VALUE_RANGE_HIGH# / #ANIM_RANGE_HIGH# * #ANIM_VALUE_HIGH_REF_LITERS# +
	} els{
		(* In between High and Low *)
		l0 #REF_LITERS_LOW# - #VALUE_RANGE_MID# / #ANIM_RANGE_MID# * #ANIM_VALUE_LOW_REF_LITERS# +
	}
} els{
	(* Under Low *)
	l0 #VALUE_RANGE_LOW# / #ANIM_RANGE_LOW# *
}

Condition.OVERRIDE_ANIM_CODE:
(A:ATTITUDE INDICATOR PITCH DEGREES, degrees) s0 0 > if{
	(* Pitch down *)
	l0 #MAX_PITCH_DISPLAYED# / 50 * 50 +
} els{
	(* Pitch up *)
	50 l0 #MIN_PITCH_DISPLAYED# / 50 * -
}
