	(M:Relative#AXIS#) (>O:_InitPos)
} els{
(M:Event) 'Unlock' scmi 0 == if{
	0 (>O:_InitPos)
} els{
(M:Event) 'LeftDrag' scmi 0 == if{
	(M:Relative#AXIS#) (O:_InitPos) - sp1
	l1 0 > if{
		l1 l0 > if{ (M:Relative#AXIS#) (>O:_InitPos) g1 }
	} els{
		l1 abs l0 > if{ (M:Relative#AXIS#) (>O:_InitPos) g2 }
	}
}
} } } }
quit
