#CALLBACKCODE_DEFAULT_IM#

UseTemplate.CALLBACKCODE:
(M:Event) 'Lock' scmp 0 == if{
	(M:RelativeY) (>O:PrevY, Number)
	(#POSITION_TYPE#:#POSITION_VAR#) (>O:InitialDraggingPosition)
	(O:InitialDraggingPosition) near (O:InitialDraggingPosition) - abs #GATE_TOLERANCE# < sp1
	#INIT_DRAG#

	#GATE_DIRECTION# -1 != if{
		l1 if{
			(O:InitialDraggingPosition) near 1 + #STEPS_NUMBER# min (>O:MaxDraggingPosition)
		} els{
			(O:InitialDraggingPosition) ceil #STEPS_NUMBER# min (>O:MaxDraggingPosition)
		}
	} els{
		#STEPS_NUMBER# (>O:MaxDraggingPosition)
	}

	#GATE_DIRECTION# 1 != if{
		l1 if{
			(O:InitialDraggingPosition) near 1 - 0 max (>O:MinDraggingPosition)
		} els{
			(O:InitialDraggingPosition) flr 0 max (>O:MinDraggingPosition)
		}
	} els{
		0 (>O:MinDraggingPosition)
	}
	#IGNORE_GATE_CODE#
	1 (>O:IsDragging)
} els{ 
	(M:Event) 'Unlock' scmp 0 == if{
		0 (>O:IsDragging)
	} els{			
		(M:Event) 'LeftDrag' scmp 0 == if{
			(M:InputType) 1 == if{
				(O:_ResetLimitsTime) (E:SIMULATION TIME, second) < if{
					(#POSITION_TYPE#:#POSITION_VAR#) (>O:InitialDraggingPosition)
					(O:InitialDraggingPosition) near (O:InitialDraggingPosition) - abs #GATE_TOLERANCE# < sp1
					#INIT_DRAG#

					#GATE_DIRECTION# -1 != if{
						l1 if{
							(O:InitialDraggingPosition) near 1 + #STEPS_NUMBER# min (>O:MaxDraggingPosition)
						} els{
							(O:InitialDraggingPosition) ceil #STEPS_NUMBER# min (>O:MaxDraggingPosition)
						}
					} els{
						#STEPS_NUMBER# (>O:MaxDraggingPosition)
					}

					#GATE_DIRECTION# 1 != if{
						l1 if{
							(O:InitialDraggingPosition) near 1 - 0 max (>O:MinDraggingPosition)
						} els{
							(O:InitialDraggingPosition) flr 0 max (>O:MinDraggingPosition)
						}
					} els{
						0 (>O:MinDraggingPosition)
					}
					#IGNORE_GATE_CODE#
				}
				(E:SIMULATION TIME, second) 0.1 + (>O:_ResetLimitsTime)
			}


			(O:PrevY) (M:RelativeY) - sp2	
			l2 #DRAG_SPEED# * sp3

			#GATE_DIRECTION# -1 != if{
				(#POSITION_TYPE#:#POSITION_VAR#) #GATE_TOLERANCE# + flr 1 + (O:MaxDraggingPosition) min (>O:MaxDraggingPosition)
			}
			#GATE_DIRECTION# 1 != if{
				(#POSITION_TYPE#:#POSITION_VAR#) #GATE_TOLERANCE# - ceil 1 - (O:MinDraggingPosition) max (>O:MinDraggingPosition)
			}
			#IGNORE_GATE_CODE#

			#NEW_POSITION# (O:MaxDraggingPosition) min (O:MinDraggingPosition) max (>#POSITION_TYPE#:#POSITION_VAR#)

			#DRAG_CODE#

			(M:RelativeY) (>O:PrevY)
		}
	}
}

(M:Event) 'WheelUp'    scmi 0 == if{ g1 } els{
(M:Event) 'WheelDown'  scmi 0 == if{ g2 } els{
(M:Event) 'LeftSingle' scmi 0 == if{ #LEFT_SINGLE_CODE# (M:Relative#AXIS#) (>O:_Last#AXIS#) } els{
(M:Event) 'LeftDrag'   scmi 0 == if{
	(O:_Last#AXIS#) (M:Relative#AXIS#) -  #DRAG_DELTA# / abs (>O:DragFactor)
	(O:_Last#AXIS#) (M:Relative#AXIS#) -  -#DRAG_DELTA# < if{ 
		(M:Relative#AXIS#) (>O:_Last#AXIS#) 
		g1 
	} els{
		(O:_Last#AXIS#) (M:Relative#AXIS#) -  #DRAG_DELTA# > if{ 
		(M:Relative#AXIS#) (>O:_Last#AXIS#) 
		g2 
		} els{
} } } } } }
quit

:#FLAG_1# (#POSITION_TYPE#:#POSITION_VAR#) #DRAG_SPEED# (O:DragFactor) * + #MAX_LIMITER# (>#POSITION_TYPE#:#POSITION_VAR#)
#UP_CODE#
quit

:#FLAG_2# (#POSITION_TYPE#:#POSITION_VAR#) #DRAG_SPEED# (O:DragFactor) * - #MIN_LIMITER# (>#POSITION_TYPE#:#POSITION_VAR#)
#DOWN_CODE#
quit
