Condition.CIRCUIT_CODE_ON:
(A:#RADIO_TYPE# VOLUME:#ID#, Percent) 0 >
(A:CIRCUIT SWITCH ON:#CIRCUIT#, Boolean) ! and if{
	#CIRCUIT# (>K:ELECTRICAL_CIRCUIT_TOGGLE)
}

Condition.CIRCUIT_CODE_OFF:
(A:#RADIO_TYPE# VOLUME:#ID#, Percent) 0 ==
(A:CIRCUIT SWITCH ON:#CIRCUIT#, Boolean) and if{
	#CIRCUIT# (>K:ELECTRICAL_CIRCUIT_TOGGLE)
}

Condition.Update:
(A:CIRCUIT NAVCOM#ID# ON, Bool) (A:LIGHT PANEL, Bool) != if{
	(A:CIRCUIT NAVCOM#ID# ON, Bool) (>K:PANEL_LIGHTS_SET)
}

True.CIRCUIT_CODE_ON:
(A:#RADIO_TYPE# VOLUME:#ID#, Percent) 0 >
(A:CIRCUIT SWITCH ON:#CIRCUIT_ID#, Boolean) ! and if{
	#CIRCUIT_ID# (>K:ELECTRICAL_CIRCUIT_TOGGLE)
}

True.CIRCUIT_CODE_OFF:
(A:#RADIO_TYPE# VOLUME:#ID#, Percent) 0 ==
(A:CIRCUIT SWITCH ON:#CIRCUIT_ID#, Boolean) and if{
	#CIRCUIT_ID# (>K:ELECTRICAL_CIRCUIT_TOGGLE)
}

True.LEFT_SINGLE_CODE:
(A:NAV SOUND:#ID#, Bool) sp0
(A:NAV SOUND:#OTHER_ID#, Bool) sp1
l0 l1 and if{
	(>K:RADIO_VOR#OTHER_ID#_IDENT_TOGGLE)
} els{
	l0 ! if{ (>K:RADIO_VOR#ID#_IDENT_TOGGLE) }
	l1 if{ (>K:RADIO_VOR#OTHER_ID#_IDENT_TOGGLE) }						
}
